<?php

namespace Damis\ExperimentBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
*
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ExperimentRepository extends EntityRepository
{
    public function getClosableExperiments($limit)
    {
        $query = $this->createQueryBuilder('e')
            ->select('e')
            ->leftJoin('e.workflowtasks', 'w')
            ->andWhere('e.status = 2')
            ->andWhere('w.workflowtaskisrunning != 0')
            ->andWhere('w.workflowtaskisrunning != 3')
            ->setMaxResults($limit);

        return $query->getQuery()->getResult();
    }
}
